use pyo3::prelude::*;
mod macros;

mod envs;
mod utils;

/// Name of the root Python module generated by this Rust extension module.
const PYTHON_MODULE_NAME: &str = "space_robotics_bench._rs";

#[pymodule]
fn _rs(m: &Bound<'_, PyModule>) -> PyResult<()> {
    envs::register(m)?;
    utils::register(m)?;
    Ok(())
}
